import React, {useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoding, setIsLoding] = useState(false);

  async function LoadMoviesHandler() {
    setIsLoding(true);
    const responce = await fetch('https://swapi.dev/api/film/');
    const data = await responce.json();

      const changedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          director: movieData.director,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovies(changedMovies);
      setIsLoding(false);
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={LoadMoviesHandler}>Load Movies</button>
      </section>
      <section>
        {!isLoding && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoding && movies.length === 0 && <h3>Oops Found Nothing</h3>}
        {isLoding && <h2>Loding...</h2>}
      </section>
    </React.Fragment>
  );
}

export default App;
